\subsection{Mathematical Formulation}

\subsubsection{Sets and Indices}
\begin{itemize}
\item $L$: Set of potential transit lines
\item $N$: Set of network nodes (stations)
\item $C$: Set of origin-destination pairs (commodities)
\item $A$: Set of arcs in the network
\end{itemize}

\subsubsection{Parameters}
\begin{itemize}
\item $c_l$: Fixed cost of establishing line $l \in L$
\item $\beta_l$: Operational cost per unit frequency for line $l \in L$
\item $k_l$: Capacity of vehicles on line $l \in L$
\item $t_l$: Round trip time of line $l \in L$
\item $d_c$: Demand of OD pair $c \in C$
\item $\theta_c$: Penalty cost for unsatisfied demand of OD pair $c \in C$
\item $f_l^{\text{min}}$: Minimum frequency required for line $l \in L$ if selected
\item $f_l^{\text{max}}$: Maximum frequency allowed for line $l \in L$
\item $V$: Total available number of vehicles
\item $a_{c,l}$: Binary parameter indicating if line $l$ can serve OD pair $c$
\item $\delta_{n,l}$: Binary parameter indicating if line $l$ passes through node $n$
\item $M$: A large enough number
\end{itemize}

\subsubsection{Decision Variables}
\begin{itemize}
\item $x_l$: Binary variable indicating if line $l$ is selected (1) or not (0)
\item $f_l$: Frequency of line $l$ (continuous)
\item $s_c$: Unsatisfied demand for OD pair $c$
\item $y_n$: Binary variable indicating if node $n$ is a transfer station
\item $z_{i,j,l}$: Binary variable indicating if arc $(i,j)$ is used in line $l$
\end{itemize}

\subsubsection{Objective Function}
Minimize total cost including operational costs and penalty for unsatisfied demand:
\begin{equation}
\text{Minimize} \quad Z = \sum_{l \in L} (c_l x_l + \beta_l f_l) + \sum_{c \in C} \theta_c s_c
\end{equation}

\subsubsection{Constraints}
\begin{enumerate}
\item \textbf{Demand Satisfaction Constraints:}
\begin{equation}
\sum_{l \in L} k_l a_{c,l} f_l + s_c \geq d_c \quad \forall c \in C
\end{equation}

\item \textbf{Frequency Bounds:}
\begin{equation}
f_l^{\text{min}} x_l \leq f_l \leq f_l^{\text{max}} x_l \quad \forall l \in L
\end{equation}

\item \textbf{Vehicle Resource Constraint:}
\begin{equation}
\sum_{l \in L} t_l f_l \leq V
\end{equation}

\item \textbf{Line Continuity Constraints:}
\begin{equation}
\sum_{j \in N} z_{i,j,l} = \sum_{j \in N} z_{j,i,l} \quad \forall i \in N, l \in L
\end{equation}

\item \textbf{Transfer Station Constraints:}
\begin{equation}
\sum_{l \in L} x_l \delta_{n,l} \geq 2y_n \quad \forall n \in N
\end{equation}

\item \textbf{Service Quality Constraints:}
\begin{equation}
\sum_{l \in L} f_l a_{c,l} \geq f_l^{\text{min}} \quad \forall c \in C
\end{equation}

\item \textbf{Line Selection and Arc Usage:}
\begin{equation}
\sum_{(i,j) \in A} z_{i,j,l} \leq M x_l \quad \forall l \in L
\end{equation}

\item \textbf{Non-negativity and Binary Constraints:}
\begin{align}
f_l &\geq 0 \quad \forall l \in L \\
s_c &\geq 0 \quad \forall c \in C \\
x_l &\in \{0,1\} \quad \forall l \in L \\
y_n &\in \{0,1\} \quad \forall n \in N \\
z_{i,j,l} &\in \{0,1\} \quad \forall (i,j) \in A, l \in L
\end{align}
\end{enumerate}
